import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';

function App() {
  const [countryData, setCountryData] = useState([]);

const [districData, setDistrictData] = useState([]);

useEffect(() => {
  // const apiUrl = `http://localhost:8080`;
  // fetch(apiUrl)
  //   .then((res) => res.json())
  //   .then((repos) => {
  //     setCountryData(repos);
  //   });
}, []);


const getCountryData = async () => {

  const result =  await axios(
    'http://localhost:8080',
  );
  setCountryData(result.data);  
}

const getDistrictData = async () => {

  const result =  await axios(
    'http://localhost:3000',
  );
  setDistrictData(result.data);  
}
  return (
    <div className="App">
      <div className="header-bar">React with Node - nginx</div>
      <header className="App-header">
      <div className="data-container">
      <div>
      <button onClick={getCountryData}> Get Country </button>
      <ul>
        {countryData.map((item,index) =>
        <li key={index}>{item.country}- {item.state}</li>
        )}
      </ul>
      </div>
      <div>
      <button onClick={getDistrictData}> Get District </button>
      <ul>
        {districData.map((item,index) =>
        <li key={index}>{item.state}- {item.district}</li>
        )}
      </ul>
      </div>
      </div>
      </header>
    </div>
  );
}

export default App;
