import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';

function App() {
  const [countryData, setCountryData] = useState([]);

const [districData, setDistrictData] = useState([{
  state:'TN',
  district: 'Vellore' 
},
{
  state:'KA',
  district: 'BANGALOE' 
}]);

useEffect(() => {
  const apiUrl = `http://localhost:8080`;
  fetch(apiUrl)
    .then((res) => res.json())
    .then((repos) => {
      setCountryData(repos);
    });
}, []);


const activateLasers = () => {
  useEffect(async () => {
    const result =  axios(
      'http://localhost:8080',
    );

    setCountryData(result);
  }, []);

}
  return (
    <div className="App">
      <header className="App-header">
      <div className="data-container">
      <div>
      <button onClick={activateLasers}> Click me </button>
      <ul>
        {countryData.map((item,index) =>
        <li key={index}>{item.country}- {item.state}</li>
        )}
      </ul>
      </div>
      <div>
      <button> Click me </button>
      <ul>
        {districData.map((item,index) =>
        <li key={index}>{item.state}- {item.district}</li>
        )}
      </ul>
      </div>
      </div>
      </header>
    </div>
  );
}

export default App;
